import React, { useState } from 'react';
import { useLocation } from 'react-router';
import history from '../../../history';
import axios from 'axios';
import Navbar from '../partials/Navbar';
import Footer from '../partials/Footer';


const Edit = ({user}) => {
    const location = useLocation();
    const { currentPost } = location.state;
    const img = currentPost.postImg ? currentPost.postImg : null; 
    const [ postTitle, setPostTitle ] = useState(currentPost.postTitle)
    const [ postImg, setPostImg ] = useState(img);


    const editPost = (e) => {
        e.preventDefault();

        axios.patch(`http://localhost:3001/post/${user._id}/${currentPost._id}`, { 
            postTitle: postTitle,
            postImg: postImg 
         });
         setTimeout(() => window.location.reload() , 130);
            history.push('/');

    }


    const renderPost = () => {
        if(img){
            return (
                <div className="w-50 container mt-5 card">
                    <div className="card p-4 text-light bg-primary my-5">
                        <h1 className="display-4">
                            Edit your post.
                        </h1>
                    </div>
                    <div className="row">
                        <form onSubmit={editPost} className="p-5 col-5">
                            <div className="form-floating mb-4">
                                <label style={{ fontSize:"20px" }} className="form-label">Post Title</label>
                                <textarea onChange={ (e) => setPostTitle(e.target.value) } style={{ height: "auto", minHeight: "100px" }} className="form-control" type="text" value={postTitle} />
                            </div>
                                <label style={{ fontSize:"20px" }} htmlFor="basic-url" className="form-label">Place a new URL for your post image</label>
                            <div className="input-group mb-4">
                                    <span className="input-group-text bg-light" id="basic-addon3"><img style={{width: "25px"}} src="data:image/png;base64,"></img></span>
                                    <input onChange={ (e) => setPostImg(e.target.value) } value={postImg} type="text" className="form-control" id="basic-url" aria-describedby="basic-addon3"></input>
                            </div>
                                <div>
                                    <button className="btn btn-primary" type="submit">Save changes</button>
                                </div>
                        </form>
    
                        <div className="col-7">
                        <label style={{ fontSize:"20px" }} className="form-label border-bottom">Preview</label>
                            <div className="row postHead">
                                <div className="avatar ml-2 btn-lg">
                                    <img id="avatar" src={user.profilePic}></img>
                                </div>
                                <div className="col-lg-10 col-md-5 col-sm-auto author mt-2 ml-0">
                                        <h5 className="mb-0">{user.userName}</h5>
                                </div>
                                <div className="postDescription m-2">
                                    <h5>{postTitle}</h5>
                                </div>
                                <div className="postImage bg-light py-1 d-flex w-100">
                                    <img 
                                    src={postImg} 
                                    className="img-fluid mx-auto"
                                    style={{height:"auto", width:"725px" }}
                                    alt={ postImg === "" ? "" : "Invalid URL" }
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }else
        if(!img){
            return (
                <div className="w-50 container mt-5 card">
                    <div className="card p-4 text-light bg-primary my-5">
                        <h1 className="display-4">
                            Edit your post.
                        </h1>
                    </div>
                    <div className="row">
                        <form onSubmit={editPost} className="p-5 col-5">
                            <div className="form-floating mb-4">
                                <label style={{ fontSize:"20px" }} className="form-label">Post Title</label>
                                <textarea onChange={ (e) => setPostTitle(e.target.value) } style={{ height: "auto", minHeight: "100px" }} className="form-control" type="text" value={postTitle} />
                            </div>
                                <div>
                                    <button className="btn btn-primary" type="submit">Save changes</button>
                                </div>
                        </form>
    
                        <div className="col-7">
                        <label style={{ fontSize:"20px" }} className="form-label border-bottom">Preview</label>
                            <div className="row postHead">
                                <div className="avatar ml-2 btn-lg">
                                    <img id="avatar" src={user.profilePic}></img>
                                </div>
                                <div className="col-lg-10 col-md-5 col-sm-auto author mt-2 ml-0">
                                        <h5 className="mb-0">{user.userName}</h5>
                                </div>
                                <div className="postDescription m-2">
                                    <h5>{postTitle}</h5>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }
    }


    return(
        <div key={currentPost._id}>
            <Navbar />
            {renderPost()}
            <Footer />
        </div>
    )
}

export default Edit;
